		private void load_data_2_grid(){						
			m_ds = new DS_GD_THONG_BAO_LAI_SUAT_IN_TIMES();
            m_us.fill_dataset_by_trai_phieu_in_times_tron_ky(m_ds, m_date_tu_ngay.Value, m_date_den_ngay.Value, m_us_v_trai_phieu.dcID);
			m_fg.Redraw = false;
			CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            CGridUtils.MakeSoTT((int)e_col_Number.STT, m_fg);
			m_fg.Redraw = true;
		}
		private void grid2us_object(US_GD_THONG_BAO_LAI_SUAT_IN_TIMES i_us
			, int i_grid_row) {
			DataRow v_dr;
			v_dr = (DataRow) m_fg.Rows[i_grid_row].UserData;
			m_obj_trans.GridRow2DataRow(i_grid_row,v_dr);
			i_us.DataRow2Me(v_dr);
		}

	
		private void us_object2grid(US_GD_THONG_BAO_LAI_SUAT_IN_TIMES i_us
			, int i_grid_row) {
			DataRow v_dr = (DataRow) m_fg.Rows[i_grid_row].UserData;
			i_us.Me2DataRow(v_dr);
			m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
		}

        private void select_trai_phieu()
        {
            f300_dm_trai_phieu v_frm300 = new f300_dm_trai_phieu();
            m_us_v_trai_phieu = v_frm300.select_trai_phieu();
            if (!m_us_v_trai_phieu.IsIDNull())
                us_trai_phieu_2_form();
        }

        private void us_trai_phieu_2_form()
        {
            if (m_us_v_trai_phieu != null)
            {                
                m_txt_ten_trai_phieu.Text = m_us_v_trai_phieu.strTEN_TRAI_PHIEU;
                m_txt_menh_gia.Text = CIPConvert.ToStr(m_us_v_trai_phieu.dcMENH_GIA, "#,###");
                m_txt_to_chuc_phat_hanh.Text = m_us_v_trai_phieu.strTEN_TO_CHUC_PHAT_HANH;                               
            }
        }		
		
        private void export_excel()
        {
            CExcelReport v_obj_export_excel = new CExcelReport("f750_Bao cao lai suat trai phieu.xls", 12, 2);
            v_obj_export_excel.AddFindAndReplaceItem("<NGAY_BAT_DAU>", CIPConvert.ToStr(m_date_tu_ngay.Value, "dd/MM/yyyy"));
            v_obj_export_excel.AddFindAndReplaceItem("<TEN_CONG_TY>", m_txt_to_chuc_phat_hanh.Text);
            v_obj_export_excel.AddFindAndReplaceItem("<NGAY_KET_THUC>", CIPConvert.ToStr(m_date_den_ngay.Value, "dd/MM/yyyy"));
            v_obj_export_excel.AddFindAndReplaceItem("<TONG_SO_NGAY>", CIPConvert.ToStr(get_tong_so_ngay(), "#,###"));            
            v_obj_export_excel.FindAndReplace(false);
            v_obj_export_excel.Export2ExcelWithoutFixedRows(m_fg, (int)e_col_Number.STT, m_fg.Cols.Count - 1, false);
        }
        private decimal get_tong_so_ngay()
        {
            decimal v_sum = 0;
            for (int i = 0; i < m_ds.GD_THONG_BAO_LAI_SUAT_IN_TIMES.Count; i++)
            {
                v_sum = v_sum + m_ds.GD_THONG_BAO_LAI_SUAT_IN_TIMES[i].SO_NGAY_AP_DUNG;
            }
            return v_sum;
        }
		private void view_gd_thong_bao_lai_suat_in_times(){			
			if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
			if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
			grid2us_object(m_us, m_fg.Row);
		//	f657_thong_bao_lai_suat_in_times_DE v_fDE = new f657_thong_bao_lai_suat_in_times_DE();			
		//	v_fDE.display(m_us);
		}		
