            US_GD_CHOT_LAI_DETAIL v_us_gd_chot_lai_detail = new US_GD_CHOT_LAI_DETAIL();
            grid2us_object(v_us_gd_chot_lai_detail, m_fg.Row);
            f250_chi_tiet_giao_dich_chot_lai v_fDE = new f250_chi_tiet_giao_dich_chot_lai();
            v_fDE.display_for_update(v_us_gd_chot_lai_detail);
            m_us_gd_chot_lai.fillDSChotLaiDetail(m_ds_gd_chot_lai_detail);
            load_data_2_grid();
        }

        private void load_cbo_ky_tinh_lai()
        {

            if (m_us_v_dm_trai_phieu != null)
            {
                decimal v_dc_so_ky_tra_lai;
                m_cbo_ky_tinh_lai.Items.Clear();
                if (m_us_v_dm_trai_phieu.dcID_DV_KY_TRA_LAI == 18)
                {
                    if(m_us_v_dm_trai_phieu.dcID_DV_KY_HAN == 18)
                        v_dc_so_ky_tra_lai = m_us_v_dm_trai_phieu.dcKY_HAN / m_us_v_dm_trai_phieu.dcKY_TRA_LAI;
                    else
                        v_dc_so_ky_tra_lai = (m_us_v_dm_trai_phieu.dcKY_HAN * 12) / m_us_v_dm_trai_phieu.dcKY_TRA_LAI;
                }
                else
                {
                    if (m_us_v_dm_trai_phieu.dcID_DV_KY_HAN == 18)
                        v_dc_so_ky_tra_lai = m_us_v_dm_trai_phieu.dcKY_HAN / (m_us_v_dm_trai_phieu.dcKY_TRA_LAI * 12);
                    else
                        v_dc_so_ky_tra_lai = m_us_v_dm_trai_phieu.dcKY_HAN / m_us_v_dm_trai_phieu.dcKY_TRA_LAI;
                }
                for (int i = 1; i <= v_dc_so_ky_tra_lai; i++)
                {
                    m_cbo_ky_tinh_lai.Items.Add(i);
                }
            }
        }

        private void load_data_ky_tinh_lai_2_from()
        {
            if (m_us_v_dm_trai_phieu != null)
            {
                int v_ky_tinh_lai = (int)m_cbo_ky_tinh_lai.SelectedItem;
                DateTime v_dat_ngay_phat_hanh_tp = m_us_v_dm_trai_phieu.datNGAY_PHAT_HANH;
                DateTime v_dat_ngay_thanh_toan;
                if (m_us_v_dm_trai_phieu.dcID_DV_KY_TRA_LAI == 18)
                {
                    v_dat_ngay_thanh_toan = v_dat_ngay_phat_hanh_tp.AddMonths(v_ky_tinh_lai * (int)m_us_v_dm_trai_phieu.dcKY_TRA_LAI);
                    m_dat_ngay_thanh_toan.Value = v_dat_ngay_thanh_toan;
                    m_date_ngay_dau_ky.Value = v_dat_ngay_phat_hanh_tp.AddMonths((v_ky_tinh_lai - 1)*(int)m_us_v_dm_trai_phieu.dcKY_TRA_LAI);                    
                }
                else
                {
                    v_dat_ngay_thanh_toan = v_dat_ngay_phat_hanh_tp.AddYears(v_ky_tinh_lai * (int)m_us_v_dm_trai_phieu.dcKY_TRA_LAI);
                    m_dat_ngay_thanh_toan.Value = v_dat_ngay_thanh_toan;
                    m_date_ngay_dau_ky.Value = v_dat_ngay_phat_hanh_tp.AddYears(0 - (int)m_us_v_dm_trai_phieu.dcKY_TRA_LAI); 
                }
                m_dat_ngay_chot_lai.Value = get_ngay_chot_lai(m_dat_ngay_thanh_toan.Value);                
                m_data_ngay_cuoi_ky.Value = m_dat_ngay_thanh_toan.Value;
                m_dat_ngay_thanh_toan_thuc_te.Value = get_ngay_thanh_toan_thuc_te(m_dat_ngay_thanh_toan.Value);
            }
        }

        private DateTime get_ngay_chot_lai(DateTime ip_ngay_thanh_toan)
        {
            US_DM_DOT_PHAT_HANH v_us_dm_dot_phat_hanh = new US_DM_DOT_PHAT_HANH(m_us_v_dm_trai_phieu.dcID_DOT_PHAT_HANH);
            DS_DM_NGAY_LAM_VIEC v_ds_dm_ng_lam_viec = new DS_DM_NGAY_LAM_VIEC();
            US_DM_NGAY_LAM_VIEC v_us_dm_ng_lam_viec = new US_DM_NGAY_LAM_VIEC();
            decimal v_so_ngay_truoc_tt = m_us_v_dm_trai_phieu.dcSO_NGAY_CHOT_LAI_TRUOC_NGAY_THANH_TOAN;
            v_us_dm_ng_lam_viec.FillDatasetGetNgayChotLai(
                v_ds_dm_ng_lam_viec,
                ip_ngay_thanh_toan,
                v_so_ngay_truoc_tt,
                v_us_dm_dot_phat_hanh.strNGAY_LAM_VIEC_HAI_SAU_YN);
            if (v_ds_dm_ng_lam_viec.DM_NGAY_LAM_VIEC == null || v_ds_dm_ng_lam_viec.DM_NGAY_LAM_VIEC.Count < v_so_ngay_truoc_tt) return ip_ngay_thanh_toan;
            return  CIPConvert.ToDatetime(CIPConvert.ToStr(v_ds_dm_ng_lam_viec.DM_NGAY_LAM_VIEC.Rows[(int)v_so_ngay_truoc_tt - 1][DM_NGAY_LAM_VIEC.NGAY]));
        }

        private DateTime get_ngay_thanh_toan_thuc_te(DateTime ip_ngay_thanh_toan)
        {
            US_DM_DOT_PHAT_HANH v_us_dm_dot_phat_hanh = new US_DM_DOT_PHAT_HANH(m_us_v_dm_trai_phieu.dcID_DOT_PHAT_HANH);
            DS_DM_NGAY_LAM_VIEC v_ds_dm_ng_lam_viec = new DS_DM_NGAY_LAM_VIEC();
            US_DM_NGAY_LAM_VIEC v_us_dm_ng_lam_viec = new US_DM_NGAY_LAM_VIEC();
            v_us_dm_ng_lam_viec.FillDatasetGetNgayThanhtoanThucTe(
                v_ds_dm_ng_lam_viec ,
                ip_ngay_thanh_toan, 
                m_us_v_dm_trai_phieu.strTHANH_TOAN_TRUOC_NGAY_LAM_VIEC_GAN_NHAT_YN,
                v_us_dm_dot_phat_hanh.strNGAY_LAM_VIEC_HAI_SAU_YN);
            if (v_ds_dm_ng_lam_viec.DM_NGAY_LAM_VIEC == null || v_ds_dm_ng_lam_viec.DM_NGAY_LAM_VIEC.Count == 0) return ip_ngay_thanh_toan;
            return CIPConvert.ToDatetime(v_ds_dm_ng_lam_viec.DM_NGAY_LAM_VIEC.Rows[0][DM_NGAY_LAM_VIEC.NGAY]);
        }
