		private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg){
			Hashtable v_htb = new Hashtable();
			v_htb.Add(GD_CHOT_LAI.ID_NGUOI_LAP, e_col_Number.ID_NGUOI_LAP);
			v_htb.Add(GD_CHOT_LAI.NGAY_CHOT_LAI, e_col_Number.NGAY_CHOT_LAI);
			v_htb.Add(GD_CHOT_LAI.ID_TRAI_PHIEU, e_col_Number.ID_TRAI_PHIEU);
			v_htb.Add(GD_CHOT_LAI.GHI_CHU1, e_col_Number.GHI_CHU1);
			v_htb.Add(GD_CHOT_LAI.NGAY_DAU_KY, e_col_Number.NGAY_DAU_KY);
            v_htb.Add(GD_CHOT_LAI.NGAY_CUOI_KY, e_col_Number.NGAY_CUOI_KY);
			v_htb.Add(GD_CHOT_LAI.NGAY_THANH_TOAN, e_col_Number.NGAY_THANH_TOAN);
			v_htb.Add(GD_CHOT_LAI.ID_NGUOI_DUYET, e_col_Number.ID_NGUOI_DUYET);
			v_htb.Add(GD_CHOT_LAI.MUC_DICH, e_col_Number.MUC_DICH);
			v_htb.Add(GD_CHOT_LAI.TRANG_THAI, e_col_Number.TRANG_THAI);
									
			ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg,v_htb,m_ds.GD_CHOT_LAI.NewRow());
			return v_obj_trans;			
		}
		private void load_data_2_grid(){						
			m_ds = new DS_GD_CHOT_LAI();
            m_us_gd_chot_lai.FillDataset(m_ds);
			m_fg.Redraw = false;
			CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            CGridUtils.MakeSoTT((int)e_col_Number.STT, m_fg);
			m_fg.Redraw = true;

            //Load ten nguoi dung
            US_HT_NGUOI_SU_DUNG v_us_nguoi_dung = new US_HT_NGUOI_SU_DUNG();
            DS_HT_NGUOI_SU_DUNG v_ds_nguoi_dung = new DS_HT_NGUOI_SU_DUNG();
            v_us_nguoi_dung.FillDataset(v_ds_nguoi_dung);
            Hashtable v_hst_nguoi_dung = new Hashtable();
            foreach (DataRow v_dr in v_ds_nguoi_dung.HT_NGUOI_SU_DUNG.Rows)
            {
                v_hst_nguoi_dung.Add(v_dr[HT_NGUOI_SU_DUNG.ID], v_dr[HT_NGUOI_SU_DUNG.TEN]);
            }
            m_fg.Cols[(int)e_col_Number.ID_NGUOI_LAP].DataMap = v_hst_nguoi_dung;
            m_fg.Cols[(int)e_col_Number.ID_NGUOI_DUYET].DataMap = v_hst_nguoi_dung;

            //Load ma trai phieu
            US_DM_TRAI_PHIEU v_us_trai_phieu= new US_DM_TRAI_PHIEU();
            DS_DM_TRAI_PHIEU v_ds_trai_phieu = new DS_DM_TRAI_PHIEU();
            v_us_trai_phieu.FillDataset(v_ds_trai_phieu);
            Hashtable v_hst_trai_phieu = new Hashtable();
            foreach (DataRow v_dr in v_ds_trai_phieu.DM_TRAI_PHIEU.Rows)
            {
                v_hst_nguoi_dung.Add(v_dr[DM_TRAI_PHIEU.ID], v_dr[DM_TRAI_PHIEU.MA_TRAI_PHIEU]);
            }
            m_fg.Cols[(int)e_col_Number.ID_TRAI_PHIEU].DataMap = v_hst_nguoi_dung;

            //Load trang thai  gd
            US_CM_DM_TU_DIEN v_us_tu_dien = new US_CM_DM_TU_DIEN();
            DS_CM_DM_TU_DIEN v_ds_tu_dien = new DS_CM_DM_TU_DIEN();
            v_us_tu_dien.FillDataset(v_ds_tu_dien);
            Hashtable v_hst_tu_dien = new Hashtable();
            foreach (DataRow v_dr in v_ds_tu_dien.CM_DM_TU_DIEN.Rows)
            {
                v_hst_tu_dien.Add(v_dr[CM_DM_TU_DIEN.ID], v_dr[CM_DM_TU_DIEN.TEN]);
            }
            m_fg.Cols[(int)e_col_Number.TRANG_THAI].DataMap = v_hst_tu_dien;
		}
        private void load_data_2_grid(string ip_str_search_key)
        {
            m_ds = new DS_GD_CHOT_LAI();
            m_us_gd_chot_lai.load_data_by_search(m_ds, ip_str_search_key);
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            CGridUtils.MakeSoTT((int)e_col_Number.STT, m_fg);
            m_fg.Redraw = true;
        }
		private void grid2us_object(US_GD_CHOT_LAI i_us, int i_grid_row) {
			DataRow v_dr;
			v_dr = (DataRow) m_fg.Rows[i_grid_row].UserData;
			m_obj_trans.GridRow2DataRow(i_grid_row,v_dr);
			i_us.DataRow2Me(v_dr);
		}
